import { createContext, useReducer } from 'react';

//# Create React Context -use React Context to save Cart Items 
// in a Global State and use it in Components

// #1 - Create Store
export const Store = createContext();

// define Initial State
const initialState = {
    cart: {
        cartItems : [], // by default there is no items in the shopping cart
    }
}

// define reducer
function reducer(state, action){
    switch(action.type){
        case 'CART_ADD_ITEM': {

            // new item
            const newItem = action.payload;


            // exist item
            const existItem = state.cart.cartItems.find(
              (item) => item._id === newItem._id
            );


            // cart items
            const cartItems = existItem
              ? state.cart.cartItems.map((item) =>
                  item.name === existItem.name ? newItem : item
                )
              : [...state.cart.cartItems, newItem];
            localStorage.setItem('cartItems', JSON.stringify(cartItems));

            // keep all values in the field ...state
            // keep all values in the Cart Object in the state ...state.cart
            // update cartItems
            return { ...state, cart: { ...state.cart, cartItems } };
          }
        default:
            return state;
    }
}

// #2 - Store Provider - it's a Wrapper for our React Application
// pass global props to children
export function StoreProvider(props){
    // define useReducer
    const [state, dispatch] = useReducer(reducer, initialState);

    //define value object
    const value = {
        state, // current state
        dispatch // action to update state in the context
    };

    return <Store.Provider value={value}>
        {props.children}
    </Store.Provider>
}
