import Axios from 'axios'
import React, { createContext, useState } from 'react';
import { Helmet } from 'react-helmet-async';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import { Link, useLocation } from 'react-router-dom';
import { Store } from '../Store';


export default function SigninScreen() {

  // useLocation - is a hook from react-router-dom
  // search - is a object inside useLocation
  const { search } = useLocation();
  // // const [email, setEmail] = useState('');
  // // const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  // from the search get there redirectInUrl 
  // by instancing URLSearchParams and passing the search object
  // and get the redirect from the query string
  const redirectInUrl = new URLSearchParams(search).get('redirect');
  const redirect = redirectInUrl ? // true
  redirectInUrl : // false
  '/';

    // access to the context
    const {state, dispatch: ctxDispatch} = createContext(Store);

  // define submitHandler function as async function
  const submitHandler = async (e) =>{
    // prevent refresh this page when user click on sign in button
    e.preventDefault();
    try 
    {
      // send an ajax request to backend for /api/users/signin
      // pass email and password for /api/users/signin as a post request
      // get the response and extract data from the response
      const { data } = await Axios.post('/api/users/signin', 
      {
        email,
        password,
      }
      );
      console.log(data);
      // dispatch an action and pass an object
      ctxDispatch(
      {
        type: 'USER_SIGNIN',
        payload: data
      }
      );
    } catch (err) {

    }

  }

  return (
    <Container className="small-container">
      <Helmet>
        <title>Sign In</title>
      </Helmet>
      <h1 className="my-3">Sign In</h1>
      <Form onSubmit={submitHandler}>
        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            onChange={(e) => setEmail(e.target.value)}
            required 
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            onChange= {(e) => setPassword(e.target.value)}
            required
          />
        </Form.Group>

        <div className="mb-3">
          <Button type="submit">Sign In</Button>
        </div>
        <div className="mb-3">
          New customer?{' '}
          <Link to={`/signup?redirect=${redirect}`}>Create your account</Link>
        </div>
      </Form>
    </Container>
  );
}
